        radial-gradient(circle at 10% 20%, hsl(43, 74%, 49%, 0.1) 0%, transparent 20%),
        radial-gradient(circle at 90% 80%, hsl(214, 32%, 81%, 0.1) 0%, transparent 20%),
        linear-gradient(135deg, hsl(223, 84%, 10%, 0.9) 0%, hsl(259, 46%, 22%, 0.8) 100%);
  }

  .overlay-dark {
    background: linear-gradient(135deg, 
        hsl(223, 84%, 2%, 0.6) 0%, 
        hsl(264, 89%, 6%, 0.7) 30%,
        hsl(259, 46%, 15%, 0.65) 60%,
        hsl(249, 39%, 6%, 0.75) 100%);
  }

  .starfield {
    position: absolute;
    width: 100%;
    height: 100%;
    background-image: 
      radial-gradient(2px 2px at 20px 30px, hsl(43, 74%, 49%, 0.6), transparent),
      radial-gradient(2px 2px at 40px 70px, hsl(214, 32%, 81%, 0.4), transparent),
