import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, timestamp, json } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
});

export const newsletters = pgTable("newsletters", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: text("email").notNull().unique(),
  subscribedAt: timestamp("subscribed_at").defaultNow().notNull(),
});

export const grimoires = pgTable("grimoires", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  title: text("title").notNull(),
  type: text("type").notNull(), // 'shadows', 'mirrors', 'stars'
  description: text("description"),
  coverImage: text("cover_image"),
  isPublic: text("is_public").default("false"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

export const grimoireEntries = pgTable("grimoire_entries", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  grimoireId: varchar("grimoire_id").notNull(),
  title: text("title").notNull(),
  content: text("content").notNull(),
  entryType: text("entry_type").notNull(), // 'spell', 'ritual', 'meditation', 'reflection', 'dream', 'divination', 'herbal', 'crystal', 'astrology'
  tags: text("tags").array(),
  mood: text("mood"), // emotional state when writing
  moonPhase: text("moon_phase"), // moon phase during entry
  astrologicalSign: text("astrological_sign"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

export const deities = pgTable("deities", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  culture: text("culture").notNull(),
  domains: json("domains").$type<string[]>().notNull(),
  elements: json("elements").$type<string[]>().notNull(),
  symbols: json("symbols").$type<string[]>().notNull(),
  epithets: json("epithets").$type<string[]>().notNull(),
  offerings: json("offerings").$type<string[]>().notNull(),
  cautions: json("cautions").$type<string[]>().notNull(),
  stories: json("stories").$type<string[]>().notNull(),
  whyMatters: text("why_matters"),
  image: text("image"),
});

export const sacredEvents = pgTable("sacred_events", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  title: text("title").notNull(),
  description: text("description"),
  category: text("category").notNull(), // ritual, seasonal, lunar, planetary, cosmic
  festivalType: text("festival_type"), // yule, ostara, beltane, solstice, etc.
  startDate: text("start_date"),
  endDate: text("end_date"),
  isRecurring: text("is_recurring").default("true"),
  recurrencePattern: text("recurrence_pattern"), // annual, lunar_cycle, etc.
  ritualFlow: json("ritual_flow"), // structured ritual instructions
  yearlyVariables: json("yearly_variables"), // dynamic elements that change per year
  tags: json("tags").$type<string[]>(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const yearlyConfigurations = pgTable("yearly_configurations", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  year: text("year").notNull().unique(),
  zodiacAnimal: text("zodiac_animal"),
  zodiacElement: text("zodiac_element"),
  intentionWord: text("intention_word"),
  lunarNewYear: text("lunar_new_year"),
  winterSolstice: text("winter_solstice"),
  springEquinox: text("spring_equinox"), 
  summerSolstice: text("summer_solstice"),
  fallEquinox: text("fall_equinox"),
  majorFullMoons: json("major_full_moons"), // dates and associated rituals
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true,
});

export const insertNewsletterSchema = createInsertSchema(newsletters).pick({
  email: true,
});

export const insertGrimoireSchema = createInsertSchema(grimoires).pick({
  title: true,
  type: true,
  description: true,
  coverImage: true,
  isPublic: true,
});

export const insertGrimoireEntrySchema = createInsertSchema(grimoireEntries).pick({
  grimoireId: true,
  title: true,
  content: true,
  entryType: true,
  tags: true,
  mood: true,
  moonPhase: true,
  astrologicalSign: true,
});

export const insertDeitySchema = createInsertSchema(deities).omit({
  id: true,
});

export const insertSacredEventSchema = createInsertSchema(sacredEvents).omit({
  id: true,
  createdAt: true,
});

export const insertYearlyConfigurationSchema = createInsertSchema(yearlyConfigurations).omit({
  id: true,
  createdAt: true,
});

// Type exports
export type User = typeof users.$inferSelect;
export type InsertUser = z.infer<typeof insertUserSchema>;

export type Newsletter = typeof newsletters.$inferSelect;
export type InsertNewsletter = z.infer<typeof insertNewsletterSchema>;

export type Grimoire = typeof grimoires.$inferSelect;
export type InsertGrimoire = z.infer<typeof insertGrimoireSchema>;

export type GrimoireEntry = typeof grimoireEntries.$inferSelect;
export type InsertGrimoireEntry = z.infer<typeof insertGrimoireEntrySchema>;

export type Deity = typeof deities.$inferSelect;
export type InsertDeity = z.infer<typeof insertDeitySchema>;

export type SacredEvent = typeof sacredEvents.$inferSelect;
export type InsertSacredEvent = z.infer<typeof insertSacredEventSchema>;

export type YearlyConfiguration = typeof yearlyConfigurations.$inferSelect;
export type InsertYearlyConfiguration = z.infer<typeof insertYearlyConfigurationSchema>;

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;
export type InsertNewsletter = z.infer<typeof insertNewsletterSchema>;
export type Newsletter = typeof newsletters.$inferSelect;
export type InsertGrimoire = z.infer<typeof insertGrimoireSchema>;
export type Grimoire = typeof grimoires.$inferSelect;
export type InsertGrimoireEntry = z.infer<typeof insertGrimoireEntrySchema>;
export type GrimoireEntry = typeof grimoireEntries.$inferSelect;
export type InsertDeity = z.infer<typeof insertDeitySchema>;
export type Deity = typeof deities.$inferSelect;
export type InsertSacredEvent = z.infer<typeof insertSacredEventSchema>;
export type SacredEvent = typeof sacredEvents.$inferSelect;
export type InsertYearlyConfiguration = z.infer<typeof insertYearlyConfigurationSchema>;
export type YearlyConfiguration = typeof yearlyConfigurations.$inferSelect;

