                radial-gradient(circle at 80% 70%, hsl(259, 46%, 15%, 0.3) 0%, transparent 50%),
                radial-gradient(circle at 50% 50%, hsl(214, 32%, 81%, 0.03) 0%, transparent 70%),
                radial-gradient(circle at 10% 80%, hsl(264, 89%, 6%, 0.4) 0%, transparent 40%);
