                      </Button>
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => handleShare(certificate.id)}
                        className="border-golden-400 text-golden-400 hover:bg-golden-400/10"
                      >
                        <Share2 className="w-4 h-4 mr-2" />
