import { useQuery } from "@tanstack/react-query";
import Navigation from "@/components/Navigation";
import ProgressCard from "@/components/ProgressCard";
import { useAuth } from "@/hooks/useAuth";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Progress as ProgressBar } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { TrendingUp, BookOpen, Award, Calendar } from "lucide-react";

export default function Progress() {
  const { user } = useAuth();

  const { data: enrollments = [] } = useQuery<any[]>({
    queryKey: ["/api/enrollments"],
  });

  const { data: journalEntries = [] } = useQuery<any[]>({
    queryKey: ["/api/journal"],
  });

  const completedEnrollments = (enrollments as any[]).filter((e: any) => e.status === "completed");
  const activeEnrollments = (enrollments as any[]).filter((e: any) => e.status === "enrolled");

  const sanctumEnrollments = activeEnrollments.filter((e: any) => e.course?.wing === "sanctum");
  const orreryEnrollments = activeEnrollments.filter((e: any) => e.course?.wing === "orrery");

  const totalXP = (user as any)?.xp || 0;
  const currentLevel = (user as any)?.level || "Neophyte";

  const getNextLevelXP = () => {
    if (totalXP >= 1500) return 1500;
    if (totalXP >= 750) return 1500;
    if (totalXP >= 250) return 750;
    return 250;
  };

  const getLevelProgress = () => {
    const nextXP = getNextLevelXP();
    return Math.min((totalXP / nextXP) * 100, 100);
  };

  const getRecentActivity = () => {
    const recentEntries = (journalEntries as any[])
      .slice(0, 5)
      .map((entry: any) => ({
        ...entry,
        type: "journal",
        date: new Date(entry.createdAt),
      }));

    const recentCompletions = completedEnrollments
      .filter((e: any) => e.completedAt)
      .slice(0, 3)
      .map((enrollment: any) => ({
        ...enrollment,
        type: "completion",
        date: new Date(enrollment.completedAt),
      }));

    return [...recentEntries, ...recentCompletions]
      .sort((a, b) => b.date.getTime() - a.date.getTime())
      .slice(0, 8);
  };

  const recentActivity = getRecentActivity();

  return (
    <div className="min-h-screen">
      <Navigation />

      <section className="relative z-10 py-16">
        <div className="max-w-7xl mx-auto px-6">
          <div className="text-center mb-12">
            <h1 className="text-4xl font-serif font-bold text-ethereal-300 mb-4">Your Sacred Journey</h1>
            <p className="text-cosmic-400">Track your progression through the mystical curriculum</p>
          </div>

          {/* Level Progress Overview */}
          <div className="grid md:grid-cols-4 gap-6 mb-12">
            <Card className="crystal-border bg-cosmic-800/50 backdrop-blur-sm border-golden-400/30">
              <CardHeader className="pb-3">
                <CardTitle className="text-golden-400 flex items-center gap-2">
                  <TrendingUp className="w-5 h-5" />
                  Current Level
                </CardTitle>
              </CardHeader>
              <CardContent>
                <p className="text-3xl font-bold text-cosmic-50 mb-2">{currentLevel}</p>
                <p className="text-sm text-cosmic-400 mb-3">{totalXP} / {getNextLevelXP()} XP</p>
                <ProgressBar value={getLevelProgress()} className="h-2" />
              </CardContent>
            </Card>

            <Card className="crystal-border bg-cosmic-800/50 backdrop-blur-sm">
              <CardHeader className="pb-3">
                <CardTitle className="text-mystical-400 flex items-center gap-2">
                  <BookOpen className="w-5 h-5" />
                  Active Courses
                </CardTitle>
              </CardHeader>
              <CardContent>
                <p className="text-3xl font-bold text-cosmic-50">{activeEnrollments.length}</p>
                <p className="text-sm text-cosmic-400">in progress</p>
              </CardContent>
            </Card>

            <Card className="crystal-border bg-cosmic-800/50 backdrop-blur-sm">
              <CardHeader className="pb-3">
                <CardTitle className="text-ethereal-400 flex items-center gap-2">
                  <Award className="w-5 h-5" />
                  Completed
                </CardTitle>
              </CardHeader>
              <CardContent>
                <p className="text-3xl font-bold text-cosmic-50">{completedEnrollments.length}</p>
                <p className="text-sm text-cosmic-400">courses finished</p>
              </CardContent>
            </Card>

            <Card className="crystal-border bg-cosmic-800/50 backdrop-blur-sm">
              <CardHeader className="pb-3">
                <CardTitle className="text-golden-400 flex items-center gap-2">
                  <Calendar className="w-5 h-5" />
                  Journal Entries
                </CardTitle>
              </CardHeader>
              <CardContent>
                <p className="text-3xl font-bold text-cosmic-50">{journalEntries.length}</p>
                <p className="text-sm text-cosmic-400">reflections written</p>
              </CardContent>
            </Card>
          </div>

          {/* Wing Progress */}
          <div className="grid lg:grid-cols-2 gap-8 mb-12">
            <Card className="crystal-border bg-cosmic-800/50 backdrop-blur-sm">
              <CardHeader>
                <CardTitle className="text-mystical-300 text-2xl font-serif">
                  Sanctum of Hidden Echoes
                </CardTitle>
                <p className="text-cosmic-400">West Wing Progress</p>
              </CardHeader>
              <CardContent className="space-y-4">
                {sanctumEnrollments.length > 0 ? (
                  sanctumEnrollments.map((enrollment: any) => (
                    <ProgressCard key={enrollment.id} enrollment={enrollment} />
                  ))
                ) : (
                  <p className="text-cosmic-500 text-center py-8">
                    No active courses in the Sanctum wing
                  </p>
                )}
              </CardContent>
            </Card>

            <Card className="crystal-border bg-cosmic-800/50 backdrop-blur-sm">
              <CardHeader>
                <CardTitle className="text-ethereal-300 text-2xl font-serif">
                  Orrery of Obscured Realms
                </CardTitle>
                <p className="text-cosmic-400">East Wing Progress</p>
              </CardHeader>
              <CardContent className="space-y-4">
                {orreryEnrollments.length > 0 ? (
                  orreryEnrollments.map((enrollment: any) => (
                    <ProgressCard key={enrollment.id} enrollment={enrollment} />
                  ))
                ) : (
                  <p className="text-cosmic-500 text-center py-8">
                    No active courses in the Orrery wing
                  </p>
                )}
              </CardContent>
            </Card>
          </div>

          {/* Recent Activity */}
          <Card className="crystal-border bg-cosmic-800/50 backdrop-blur-sm">
            <CardHeader>
              <CardTitle className="text-ethereal-300 text-2xl font-serif">
                Recent Activity
              </CardTitle>
              <p className="text-cosmic-400">Your latest achievements and reflections</p>
            </CardHeader>
            <CardContent>
              {recentActivity.length > 0 ? (
                <div className="space-y-4">
                  {recentActivity.map((activity: any, index) => (
                    <div key={index} className="flex items-center justify-between p-4 bg-cosmic-700/50 rounded-xl">
                      <div className="flex items-center gap-4">
                        <div className={`w-10 h-10 rounded-full flex items-center justify-center ${
                          activity.type === "journal" 
                            ? "bg-golden-600" 
                            : "bg-green-600"
                        }`}>
                          {activity.type === "journal" ? (
                            <Calendar className="w-5 h-5 text-white" />
                          ) : (
                            <Award className="w-5 h-5 text-white" />
                          )}
                        </div>
                        <div>
                          <p className="font-medium text-cosmic-50">
                            {activity.type === "journal" 
                              ? "Journal Entry"
                              : `Completed ${activity.course?.title}`
                            }
                          </p>
                          <p className="text-sm text-cosmic-400">
                            {activity.date.toLocaleDateString()}
                          </p>
                        </div>
                      </div>
                      <Badge variant="outline" className="text-cosmic-400">
                        {activity.type === "journal" ? "+10 XP" : `+${activity.course?.xpReward || 0} XP`}
                      </Badge>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-cosmic-500 text-center py-8">
                  No recent activity. Start your mystical journey!
                </p>
              )}
            </CardContent>
          </Card>
        </div>
      </section>
    </div>
  );
}

client/src/pages/athenaeum-student-login.content:
import { useState } from "react";
import { motion } from "framer-motion";
import { Link, useLocation } from "wouter";
import { ArrowLeft, Lock, Mail, Eye, EyeOff, BookOpen, GraduationCap } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import backgroundImage from "@assets/background_1755498699765.webp";

export default function AthenaeumStudentLogin() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [, setLocation] = useLocation();
  const { toast } = useToast();

  const fadeInUp = {
    initial: { opacity: 0, y: 30 },
    animate: { opacity: 1, y: 0 },
    transition: { duration: 1, ease: "easeOut" }
  };

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      // Call the auth API for students/teachers
      const response = await apiRequest("POST", "/api/auth/login", {
        email,
        password
      });

      const data = await response.json();

      if (response.ok) {
        // Store user session
        localStorage.setItem("athenaeum_user", "true");
        localStorage.setItem("user_email", email);
        localStorage.setItem("user_role", data.role || "student");
        localStorage.setItem("user_name", data.name || "");

        toast({
          title: "Sacred Portal Opened",
          description: `Welcome to the Athenaeum, ${data.role === 'instructor' ? 'Esteemed Teacher' : 'Dear Student'}.`,
          duration: 5000,
        });

        // Redirect to full Athenaeum based on role
        setTimeout(() => {
          if (data.role === 'instructor') {
            setLocation("/athenaeum/instructor");
          } else {
            setLocation("/athenaeum/student");
          }
        }, 1000);
      } else {
        throw new Error(data.message || "Login failed");
      }
    } catch (error) {
      toast({
        title: "Access Denied",
        description: "The mystical guardians do not recognize these credentials. Please check your email and password.",
        variant: "destructive",
        duration: 5000,
      });
    }

    setIsLoading(false);
  };

  return (
    <section 
      className="min-h-screen text-ethereal-white flex items-center justify-center" 
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundAttachment: 'fixed'
      }}
    >
      <div className="absolute inset-0 bg-black/80"></div>

      {/* Navigation */}
      <nav className="absolute top-0 left-0 right-0 z-50 p-6">
        <Link 
          href="/athenaeum"
          className="inline-flex items-center text-golden-rune hover:text-silver-star transition-colors group"
        >
          <ArrowLeft className="w-5 h-5 mr-2 group-hover:translate-x-[-2px] transition-transform" />
          â† Back to Athenaeum
        </Link>
      </nav>

      <div className="relative z-10 w-full max-w-md px-6">
        <motion.div
          {...fadeInUp}
          className="mystical-border p-8 rounded-lg grimoire-texture text-center"
        >
          <div className="w-20 h-20 mx-auto mb-6 bg-gradient-to-br from-mystical-400 to-ethereal-400 rounded-full flex items-center justify-center">
            <BookOpen className="w-10 h-10 text-void-black" />
          </div>

          <h1 className="font-gothic text-3xl text-golden-rune mb-4">
            Athenaeum Portal
          </h1>

          <p className="text-silver-star/90 mb-8">
            Enter your sacred credentials to access your mystical learning journey.
          </p>

          <form onSubmit={handleLogin} className="space-y-6">
            <div className="relative">
              <Mail className="absolute left-3 top-3 w-5 h-5 text-silver-star/60" />
              <Input
                type="email"
                placeholder="Student or teacher email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="pl-10 bg-mystical-600/30 border border-ethereal-400/50 rounded-lg px-6 py-4 text-ethereal-white placeholder:text-silver-star/70 focus:border-ethereal-400 focus:outline-none transition-colors"
                required
              />
            </div>

            <div className="relative">
              <Lock className="absolute left-3 top-3 w-5 h-5 text-silver-star/60" />
              <Input
                type={showPassword ? "text" : "password"}
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="pl-10 pr-10 bg-mystical-600/30 border border-ethereal-400/50 rounded-lg px-6 py-4 text-ethereal-white placeholder:text-silver-star/70 focus:border-ethereal-400 focus:outline-none transition-colors"
                required
              />
              <button
                type="button"
                onClick={() => setShowPassword(!showPassword)}
                className="absolute right-3 top-3 text-silver-star/60 hover:text-ethereal-400 transition-colors"
              >
                {showPassword ? <EyeOff className="w-5 h-5" /> : <Eye className="w-5 h-5" />}
              </button>
            </div>

            <Button
              type="submit"
              disabled={isLoading}
              className="w-full bg-gradient-to-r from-mystical-500 to-ethereal-500 hover:from-mystical-400 hover:to-ethereal-400 text-void-black font-semibold py-3 transition-all duration-500 hover:scale-105 relative z-50"
              style={{ pointerEvents: 'auto' }}
            >
              {isLoading ? "Seeking Permission..." : "Enter the Athenaeum"}
            </Button>
          </form>

          <div className="mt-6 space-y-4">
            <div className="p-4 bg-mystical-500/10 border border-mystical-400/30 rounded-lg">
              <p className="text-mystical-400 text-sm font-semibold flex items-center justify-center">
                <GraduationCap className="w-4 h-4 mr-2" />
                Students & Teachers
              </p>
              <p className="text-silver-star/70 text-xs mt-1">
                Access your courses, progress, and mystical learning materials.
              </p>
            </div>

            <div className="text-center">
              <Link
                href="/athenaeum/register"
                className="text-ethereal-400 hover:text-golden-rune text-sm transition-colors"
              >
                Don't have an account? Request enrollment â†’
              </Link>
            </div>
          </div>
        </motion.div>
      </div>
    </section>
  );
}
